import React from 'react';
import { Link } from 'react-router-dom';

const Home = () => {
  const categories = [
    {
      id: 1,
      title: 'Premium Beef Steak',
      image: '../public/images/kucuket.png',
      link: '/products?category=premium-beef'
    },
    {
      id: 2,
      title: 'İşlenmiş Etler',
      image: '../public/images/kucuket.png',
      link: '/products?category=processed-meat'
    },
    {
      id: 3,
      title: 'Kasap Çeşitleri',
      image: '../public/images/kucuket.png',
      link: '/products?category=butcher'
    },
    {
      id: 4,
      title: 'Şarküteri Çeşitleri',
      image: '../public/images/kucuket.png',
      link: '/products?category=charcuterie'
    },
    {
      id: 5,
      title: 'Pastırma',
      image: '../public/images/kucuket.png',
      link: '/products?category=pastirma'
    },
    {
      id: 6,
      title: 'Sucuk',
      image: '../public/images/kucuket.png',
      link: '/products?category=sucuk'
    },
    {
      id: 7,
      title: 'Köfte',
      image: '../public/images/kucuket.png',
      link: '/products?category=kofte'
    },
    {
      id: 8,
      title: 'Yan Ürünler',
      image: '../public/images/kucuket.png',
      link: '/products?category=side-products'
    }
  ];

  const features = [
    {
      id: 1,
      title: 'Hızlı Teslimat',
      description: 'Eskişehir İçi ÜCRETSİZ Teslimat, Eskişehir Dışı 500TL ve Üzeri Alışverişlerinizde ÜCRETSİZ Kargo',
      icon: 'fas fa-truck'
    },
    {
      id: 2,
      title: 'Özenli Paketleme',
      description: 'Siparişleriniz Özel Ambalajlarımız İle Özenle Paketlenmektedir',
      icon: 'fas fa-box'
    },
    {
      id: 3,
      title: 'Müşteri Hizmetleri',
      description: 'Her Gün 09:00-18:00 Arası Sizlere Yardımcı Olmaktayız',
      icon: 'fas fa-headset'
    },
    {
      id: 4,
      title: 'Güvenli Ödeme',
      description: '3D Güvenlik Altyapımız İle Kart Bilgileriniz Güvendedir',
      icon: 'fas fa-shield-alt'
    }
  ];

  return (
    <div>
      {/* Hero Section */}
      <section className="hero">
        <div className="hero-content">
          <h1>Premium Et Ürünleri</h1>
          <p>En kaliteli et ürünleri ve kasap hizmetleri ile hizmetinizdeyiz.</p>
          <Link to="/products" className="btn btn-primary">Ürünlerimizi İncele</Link>
        </div>
      </section>

      {/* Categories Section */}
      <section className="categories">
        <div className="container">
          <h2 className="section-title">Ürün Kategorilerimiz</h2>
          <div className="category-grid">
            {categories.map(category => (
              <Link to={category.link} key={category.id} className="category-card">
                <div className="category-image">
                  <img src={category.image} alt={category.title} />
                </div>
                <h3>{category.title}</h3>
              </Link>
            ))}
          </div>
        </div>
      </section>

      {/* Features Section */}
      <section className="features">
        <div className="container">
          <h2 className="section-title">Neden Bizi Tercih Etmelisiniz?</h2>
          <div className="feature-grid">
            {features.map(feature => (
              <div key={feature.id} className="feature-card">
                <i className={`${feature.icon} feature-icon`}></i>
                <h3>{feature.title}</h3>
                <p>{feature.description}</p>
              </div>
            ))}
          </div>
        </div>
      </section>
    </div>
  );
};

export default Home;
