import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';

const Home = () => {
  const [currentSlide, setCurrentSlide] = useState(0);
  const slides = [
    {
      title: "ESKİŞEHİR 'İN EN İYİ KASABI",
      subtitle: "Kalite & Lezzet Bir Arada...",
      description: "LEZZET DOLU MUTFAKLARIN VAZGEÇİLMEZ TEDARİKÇİSİ",
      background: "/images/kirmizi-et.png",
      className: "first-slide"
    },
    {
      title: "20 YILI AŞKIN",
      subtitle: "Severek Tükettiğiniz",
      description: "Başpınarlar Et Entegre & Steak House ürünleri artık 7/24 bir telefon kadar yanınızda.",
      background: "/images/baspinarlar-severlerin-adresi-1.png",
      className: "second-slide"
    }
  ];

  useEffect(() => {
    const interval = setInterval(() => {
      setCurrentSlide((prev) => (prev + 1) % slides.length);
    }, 5000);
    return () => clearInterval(interval);
  }, []);

  const categories = [
    {
      id: 1,
      title: 'Dana Eti Grubu',
      description: 'Dana kıyma, dana saç kavurma, inegöl köfte, şişlik et, kemiksiz incik, pirzola, dilimli pastırma, dana kontrafile gibi çeşit çeşit ürünler ile her öğününüzü...',
      image: '/images/kucuket.png',
      link: '/products?category=dana'
    },
    {
      id: 2,
      title: 'Kuzu Eti Grubu',
      description: 'Kuzu biftek, kuzu kıyma, kuzu pirzola, kuzu bonfile, kuzu şişlik et, kuzu incik, kuzu but fırın, kuzu saç kavurma ürün çeşitlerimizle hizmetinizdeyiz...',
      image: '/images/kucuket.png',
      link: '/products?category=kuzu'
    },
    {
      id: 3,
      title: 'Tavuk Eti Grubu',
      description: 'Beyaz et çeşitleri tavuk eti, tavuk but, tavuk kanat, tavuk kelebek, tavuk sote, tüm tavuk, mangallık tavuk ürünleri, piliç baget, etpiliç tavuk, helal kesim tavuk ürünleri',
      image: '/images/kucuket.png',
      link: '/products?category=tavuk'
    },
    {
      id: 4,
      title: 'Sucuk Grubu',
      description: 'Vadi Kasabı sucukları ev sucuğu tadında kaliteli sucuk çeşitleri. Dana sucuk, dana acılı ve acısız sucuk çeşitleri, kangal sucuk, mangallık sucuk, göden sucuk',
      image: '/images/kucuket.png',
      link: '/products?category=sucuk'
    }
  ];

  const features = [
    {
      id: 1,
      title: 'Hızlı Teslimat',
      description: 'Eskişehir İçi ÜCRETSİZ Teslimat, Eskişehir Dışı 500TL ve Üzeri Alışverişlerinizde ÜCRETSİZ Kargo',
      icon: 'fas fa-truck'
    },
    {
      id: 2,
      title: 'Özenli Paketleme',
      description: 'Siparişleriniz Özel Ambalajlarımız İle Özenle Paketlenmektedir',
      icon: 'fas fa-box'
    },
    {
      id: 3,
      title: 'Müşteri Hizmetleri',
      description: 'Her Gün 09:00-18:00 Arası Sizlere Yardımcı Olmaktayız',
      icon: 'fas fa-headset'
    },
    {
      id: 4,
      title: 'Güvenli Ödeme',
      description: '3D Güvenlik Altyapımız İle Kart Bilgileriniz Güvendedir',
      icon: 'fas fa-shield-alt'
    }
  ];

  return (
    <div>
      {/* Hero Section */}
      <section className="hero">
        {slides.map((slide, index) => (
          <div
            key={index}
            className={`hero-background ${currentSlide === index ? 'active' : ''} ${slide.className}`}
            style={{ backgroundImage: `url(${slide.background})` }}
          />
        ))}
        <div className="hero-content">
          {slides.map((slide, index) => (
            <div key={index} className={`hero-slide ${currentSlide === index ? 'active' : ''} ${slide.className}`}>
              <h1>{slide.title}</h1>
              <p>{slide.subtitle}</p>
              <p>{slide.description}</p>
            </div>
          ))}
          <Link to="/products" className="btn btn-primary">Ürünlerimizi İncele</Link>
        </div>
      </section>

      {/* Categories Section */}
      <section className="categories">
        <div className="container">
          <h2 className="section-title">ÜRÜNLERİMİZ</h2>
          <p className="section-subtitle">neler yapıyoruz?</p>
          <p className="section-description">
            Vadi Kasabı Konya'da dana eti, kuzu eti, tavuk eti, köfte çeşitleri, sucuk çeşitleri, mangallık et ürünleri
            ve daha bir çok ürün çeşidi ile hizmet vermektedir...
          </p>
          <div className="category-grid">
            {categories.map(category => (
              <Link to={category.link} key={category.id} className="category-card">
                <div className="category-image">
                  <img src={category.image} alt={category.title} />
                </div>
                <div className="category-info">
                  <h3>{category.title}</h3>
                  <p>{category.description}</p>
                </div>
              </Link>
            ))}
          </div>
        </div>
      </section>

      {/* Call to Action Section */}
      <section className="cta-section">
        <div className="container">
          <h2>Bu gün mangalda ne yapsak?</h2>
          <p>Hijyen, güvenilir et ürünlerini nerede bulabilirim?</p>
          <p>gibi sorularınızın cevabı Konya'da Vadi Kasabında...</p>
          <Link to="/contact" className="btn btn-primary">Sipariş Ver</Link>
        </div>
      </section>

      {/* Features Section */}
      <section className="features">
        <div className="container">
          <h2 className="section-title">Neden Bizi Tercih Etmelisiniz?</h2>
          <div className="feature-grid">
            {features.map(feature => (
              <div key={feature.id} className="feature-card">
                <i className={`${feature.icon} feature-icon`}></i>
                <h3>{feature.title}</h3>
                <p>{feature.description}</p>
              </div>
            ))}
          </div>
        </div>
      </section>
    </div>
  );
};

export default Home;
